    "SYDE 162": {
        "title": "Human Factors in Design",
        "label": [],
        "description": "Design of human-machine environments, design to reduce human error. Analytical methods of determining user needs in systems with humans. Information processing and human sensory processes and consideration of these elements in the design of systems with humans. Human physical capabilities and consideration of these in ergonomic design. [Offered: S]",
        "subjectName": "Systems Design Engineering",
        "subjectCode": "SYDE"
    },
    "SYDE 181": {
        "title": "Physics 1 (Statics)",
        "label": [],
        "description": "Basic concepts of mechanics, vectors. Statics of particles. Rigid bodies and force systems, equilibrium of rigid bodies. Analysis of trusses and frames. Distributed forces, centroids and moments of inertia. Friction. Virtual work. [Offered: F]",
        "subjectName": "Systems Design Engineering",
        "subjectCode": "SYDE"
    },
    "SYDE 182": {
        "title": "Physics 2 (Dynamics)",
        "label": [],
        "description": "Kinematics of particles, rectilinear and curvilinear motion. Kinetics of particles, application to space mechanics. Energy and momentum methods. Systems of particles. Kinematics and kinetics of rigid bodies; planar motion. Vibrations. [Offered: F, W, S]",
        "subjectName": "Systems Design Engineering",
        "subjectCode": "SYDE"
    },
    "SYDE 192": {
        "title": "Digital Systems",
        "label": [],
        "description": "Digital technology, combinatorial logic, binary arithmetic, synchronous sequential circuits, design methodology, algorithmic state machines, microcomputer interfacing. [Offered: S]",
        "subjectName": "Systems Design Engineering",
        "subjectCode": "SYDE"
    },
